import React, { useEffect, useState } from 'react';
import { Redirect, Route } from 'react-router-dom';
import './App.css';
import SignIn from './SignIn.js';
import SignUp from './SignUp.js';
import Main from './Main'
import {withCookies, useCookies} from 'react-cookie';

function App() {
  let [cookie, SetCookie] = useCookies(['user']);
  let [hasCookie, setHasCookie] = useState(false);

  useEffect(()=>{
    if(cookie.user && cookie.user !== 'undefined'){
      setHasCookie(true);
    }
  },[cookie]);

  return (
    <div className="App">
      {!hasCookie ? <Redirect to="/" /> : <Redirect to="Main" />}
    <Route exact path="/">
     <SignIn />
    </Route>
    <Route path="/SignUp">
      <SignUp />
    </Route>
    </div>


  );
}

export default App;
