import React, { useEffect, useState } from 'react';
import { Redirect, Route } from 'react-router-dom';
import './App.css';
import SignIn from './SignIn.js';
import SignUp from './SignUp.js';
import Main from './Main.js';
import {withCookies, useCookies} from 'react-cookie';

function App() {
  let [cookie, SetCookie] = useCookies(['user']);
  let [hasCookie, setHasCookie] = useState(false);

  useEffect(()=>{
    if(cookie.user && cookie.user !== 'undefined'){
      setHasCookie(true);
    }
  },[]);

  return (
    <div className="App">
      {!hasCookie ? <Redirect to="/" /> : <Redirect to="/main" />}
    <Route exact path="/" render={routerProps =>{
      return(
      <SignIn 
      {...routerProps}
      setHasCookie={setHasCookie}
      />
      );
    }}>
    </Route>
    <Route path="/SignUp">
      <SignUp />
    </Route>
    <Route path="/main" render={routerProps =>{
      return(
        <Main
        {...routerProps}
        setHasCookie={setHasCookie}
        SetCookie={()=>{
          SetCookie('user');
          setHasCookie(false);
        }}
        />
      );
    }}>
    </Route>
    </div>
  );
}

export default withCookies(App);
